import { Component, Input, OnInit} from '@angular/core';
import { Recipe } from '../../recipe.model';
import { RecipeService } from '../../recipe.service';

@Component({
  selector: 'app-recipe-item',
  templateUrl: './recipe-item.component.html',
  styleUrls: ['./recipe-item.component.css']
})
export class RecipeItemComponent {
  @Input("recipeData") data: Recipe;
  name : string;
  description: string;
  imagePath: string;

  constructor(private recipeService: RecipeService){
  }

  ngOnInit(){
    this.name = this.data.name;
    this.description = this.data.description;
    this.imagePath = this.data.imagePath;
  }

  onSelected(){
    this.recipeService.recipeSelected.emit(this.data);
  }
}
<a style="cursor: pointer;" class="list-group-item clearfix" (click)="onSelected()">
    <div class="pull-left">
        <h4 class="list-group-heading">{{name}}</h4>
        <p class="list-group-item-text">{{description}}</p>
    </div>
    <span class="pull-right">
        <img class="img-responsive" alt="{{name}}" style="max-height: 50px;" [src]="imagePath">
    </span>
</a>
